import React from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import axios from 'axios'; // Import axios for making API requests
import { useForm } from 'react-hook-form';
import AutoLayout from '../components/AutoLayout';
import '../style/style.css';

const Contact = () => {
  const { register, handleSubmit, formState: { errors } } = useForm();

  const onSubmit = async (data) => {
    try {
      // Make the API request to SendGrid
      // ... (same as the previous example) ...
    } catch (error) {
      console.error('Error sending email:', error);
    }
  };

  return (
    <AutoLayout>
      <Container>
        <Row id="section3" className="section">
          <Col>
            <h2 style={{ fontWeight: 'bold' }}>Contact Me</h2>
            <Form onSubmit={handleSubmit(onSubmit)}>
              {/* ... Form content ... */}
            </Form>
            <p>Email: <a href="mailto:miccibenner@gmail.com">miccibenner@gmail.com</a></p>
            <p>Phone: <a href="tel:5406568132">540-656-8132</a></p>
          </Col>
        </Row>
      </Container>
    </AutoLayout>
  );
};

export default Contact;
